2.9.example:
# Define a variable to check
temperature = 25

# Conditional execution with if, elif, and else
if temperature > 30:
    print("It's a hot day. Stay hydrated!")
elif 20 <= temperature <= 30:
    print("The weather is pleasant today.")
else:
    print("It's quite cool outside. Wear a jacket.")

# Simple condition
age = 18
if age >= 18:
    print("You are eligible to vote.")

# Nested if statements
score = 85
if score >= 60:
    if score >= 90:
        print("Excellent performance!")
    elif score >= 75:
        print("Good job!")
    else:
        print("You passed, but there's room for improvement.")
else:
    print("Unfortunately, you failed. Better luck next time.")

2.14.example:
# Example 1: Using 'break' to exit a loop
print("Break example:")
for number in range(1, 10):
    if number == 5:
        print("Breaking the loop at:", number)
        break  # Exit the loop
    print("Number:", number)

# Example 2: Using 'continue' to skip an iteration
print("\nContinue example:")
for number in range(1, 10):
    if number % 2 == 0:
        continue  # Skip even numbers
    print("Odd number:", number)

# Example 3: Using 'pass' as a placeholder
print("\nPass example:")
for number in range(1, 5):
    if number == 3:
        pass  # Do nothing here
    else:
        print("Number:", number)

5.42.example:
# Example 1: Basic inheritance
class Animal:
    def __init__(self, name):
        self.name = name  # Common attribute for all animals

    def eat(self):
        print(f"{self.name} is eating.")

# Child class inheriting from the Animal class
class Dog(Animal):
    def bark(self):
        print(f"{self.name} is barking.")

# Creating an instance of the child class
dog = Dog("Buddy")
dog.eat()  # Inherited method from the Animal class
dog.bark()  # Method defined in the Dog class

# Example 2: Adding unique attributes in the child class
class Cat(Animal):
    def __init__(self, name, color):
        self.name = name  # Manually initializing the parent class attribute
        self.color = color  # Additional attribute for Cat

    def meow(self):
        print(f"{self.name} is meowing. It has {self.color} fur.")

# Creating an instance of the Cat class
cat = Cat("Whiskers", "white")
cat.eat()  # Inherited method from the Animal class
cat.meow()  # Method defined in the Cat class

5.43.example:
# Example 1: Multilevel inheritance with three levels
class Animal:
    def __init__(self, name):
        self.name = name

    def eat(self):
        print(f"{self.name} is eating.")

# First level of inheritance
class Mammal(Animal):
    def walk(self):
        print(f"{self.name} is walking.")

# Second level of inheritance
class Dog(Mammal):
    def bark(self):
        print(f"{self.name} is barking.")

# Creating an object of the grandchild class
dog = Dog("Buddy")
dog.eat()   # Inherited from Animal
dog.walk()  # Inherited from Mammal
dog.bark()  # Defined in Dog

# Example 2: Adding specific features at each level
class Vehicle:
    def start(self):
        print("The vehicle has started.")

class Car(Vehicle):
    def drive(self):
        print("The car is being driven.")

class ElectricCar(Car):
    def charge(self):
        print("The electric car is charging.")

# Creating an object of the grandchild class
tesla = ElectricCar()
tesla.start()  # Inherited from Vehicle
tesla.drive()  # Inherited from Car
tesla.charge()  # Defined in ElectricCar

