5.46.example:
# Example 1: Basic method chaining
class Person:
    def __init__(self, name):
        self.name = name

    def set_age(self, age):
        self.age = age
        return self  # Returning the instance to enable chaining

    def set_city(self, city):
        self.city = city
        return self  # Returning the instance to enable chaining

    def introduce(self):
        print(f"My name is {self.name}, I am {self.age} years old, and I live in {self.city}.")
        return self  # Returning the instance for further chaining if needed

# Using method chaining to configure and introduce a person
person = Person("Alice").set_age(25).set_city("New York").introduce()

# Example 2: Method chaining for transformations
class TextProcessor:
    def __init__(self, text):
        self.text = text

    def to_upper(self):
        self.text = self.text.upper()
        return self  # Returning the instance

    def replace_spaces(self):
        self.text = self.text.replace(" ", "_")
        return self  # Returning the instance

    def print_text(self):
        print(self.text)
        return self  # Returning the instance

# Using method chaining for text transformations
TextProcessor("Hello World").to_upper().replace_spaces().print_text()

5.47.example:
# Example 1: Using super() in a constructor
class Animal:
    def __init__(self, name):
        self.name = name
        print(f"Animal {self.name} is initialized.")

class Dog(Animal):
    def __init__(self, name, breed):
        super().__init__(name)  # Call the parent class constructor
        self.breed = breed
        print(f"Dog of breed {self.breed} is initialized.")

# Creating an instance of the Dog class
dog = Dog("Buddy", "Golden Retriever")

# Example 2: Using super() to access an overridden method
class Parent:
    def show(self):
        print("This is a method in Parent class.")

class Child(Parent):
    def show(self):
        print("This is a method in Child class.")
        super().show()  # Call the overridden method in the Parent class

# Creating an instance of the Child class
child = Child()
child.show()

5.48.example:
from abc import ABC, abstractmethod

# Example 1: Defining an abstract class with an abstract method
class Animal(ABC):  # Inheriting from ABC to define an abstract class
    @abstractmethod
    def sound(self):
        pass  # Abstract method with no implementation

# Concrete subclass implementing the abstract method
class Dog(Animal):
    def sound(self):
        print("Dog barks.")

class Cat(Animal):
    def sound(self):
        print("Cat meows.")

# Creating instances of the subclasses
dog = Dog()
dog.sound()

cat = Cat()
cat.sound()

# Example 2: Abstract class with both concrete and abstract methods
class Vehicle(ABC):
    def __init__(self, brand):
        self.brand = brand

    @abstractmethod
    def start(self):
        pass  # Abstract method

    def show_brand(self):
        print(f"Vehicle brand: {self.brand}")

class Car(Vehicle):
    def start(self):
        print(f"The {self.brand} car is starting.")

# Creating an instance of the subclass
car = Car("Tesla")
car.show_brand()  # Calls the concrete method from the abstract class
car.start()  # Calls the implementation from the Car class
