import os

# Example 1: Checking if a file or directory exists
path = "example.txt"
if os.path.exists(path):
    print(f"The path '{path}' exists.")
else:
    print(f"The path '{path}' does not exist.")

# Example 2: Checking if a path is a file
file_path = "example.txt"
if os.path.isfile(file_path):
    print(f"The path '{file_path}' is a file.")
else:
    print(f"The path '{file_path}' is not a file.")

# Example 3: Checking if a path is a directory
directory_path = "example_folder"
if os.path.isdir(directory_path):
    print(f"The path '{directory_path}' is a directory.")
else:
    print(f"The path '{directory_path}' is not a directory.")

# Example 4: Combining checks for robust file operations
path = "data.txt"
if os.path.exists(path) and os.path.isfile(path):
    print(f"Ready to perform operations on the file: {path}")
else:
    print(f"The file '{path}' is missing or not a regular file.")
